import * as XLSX from 'xlsx';
import { MerchantMetadata } from '@shared/schema';

export interface LeadsParseResult {
  success: boolean;
  data?: MerchantMetadata[];
  errors: string[];
  warnings: string[];
}

function normalizeColumnName(name: string): string {
  return name.toLowerCase().trim().replace(/\s+/g, ' ');
}

function generateTempMerchantId(): string {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  let result = 'LEAD-';
  for (let i = 0; i < 8; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return result;
}

// Parse Leads file (MyLeads Excel)
export async function parseLeadsFile(file: File): Promise<LeadsParseResult> {
  const errors: string[] = [];
  const warnings: string[] = [];

  try {
    // Read Excel file
    const arrayBuffer = await file.arrayBuffer();
    const workbook = XLSX.read(arrayBuffer, { type: 'array' });

    // Get first sheet
    const sheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[sheetName];

    // Convert to JSON
    const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 }) as any[][];

    if (jsonData.length === 0) {
      errors.push('File is empty');
      return { success: false, errors, warnings };
    }

    const headers = jsonData[0] as string[];
    const rows = jsonData.slice(1);

    // Find column indices
    const getColumnIndex = (columnNames: string[]) => {
      return headers.findIndex(h => 
        columnNames.some(name => normalizeColumnName(h).includes(normalizeColumnName(name)))
      );
    };

    const midIndex = getColumnIndex(['Existing MID', 'MID']);
    const dbaIndex = getColumnIndex(['DBA']);
    const branchIndex = getColumnIndex(['Partner Branch Number', 'Branch Number']);
    const statusIndex = getColumnIndex(['Status']);
    const statusCategoryIndex = getColumnIndex(['Status Category']);
    const processorIndex = getColumnIndex(['Current Processor', 'Processor']);

    if (dbaIndex === -1) {
      errors.push('Required column not found: DBA is required');
      return { success: false, errors, warnings };
    }

    const metadata: MerchantMetadata[] = [];
    let generatedIdsCount = 0;

    rows.forEach((row, index) => {
      const dba = row[dbaIndex]?.toString().trim();

      if (!dba) {
        warnings.push(`Row ${index + 2}: Missing DBA, skipping`);
        return;
      }

      let mid = midIndex !== -1 ? row[midIndex]?.toString().trim() : '';

      if (!mid) {
        mid = generateTempMerchantId();
        generatedIdsCount++;
      }

      metadata.push({
        merchantId: mid,
        dbaName: dba,
        partnerBranchNumber: branchIndex !== -1 ? row[branchIndex]?.toString().trim() : undefined,
        status: statusIndex !== -1 ? row[statusIndex]?.toString().trim() : undefined,
        statusCategory: statusCategoryIndex !== -1 ? row[statusCategoryIndex]?.toString().trim() : undefined,
        currentProcessor: processorIndex !== -1 ? row[processorIndex]?.toString().trim() : undefined,
      });
    });

    if (generatedIdsCount > 0) {
      warnings.push(`Auto-generated temporary IDs for ${generatedIdsCount} leads without Existing MID`);
    }

    if (metadata.length === 0) {
      errors.push('No valid merchant metadata found in file');
      return { success: false, errors, warnings };
    }

    return { success: true, data: metadata, errors, warnings };

  } catch (error) {
    errors.push(`Failed to parse leads file: ${error instanceof Error ? error.message : 'Unknown error'}`);
    return { success: false, errors, warnings };
  }
}

client/src/components/at-risk-merchants..content:
import { MerchantRecord } from '@shared/schema';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { formatCurrency, formatPercent, getRevenue } from '@/lib/analytics';
import { AlertTriangle, TrendingDown, HelpCircle } from 'lucide-react';

interface AtRiskMerchant {
  merchantId: string;
  merchantName: string;
  currentRevenue: number;
  previousRevenue: number;
  declinePercent: number;
  consecutiveDeclines: number;
  riskLevel: 'critical' | 'high' | 'medium';
}

interface AtRiskMerchantsProps {
  records: MerchantRecord[];
  currentMonth?: string | null;
  limit?: number;
}

export function AtRiskMerchants({ records, currentMonth, limit = 10 }: AtRiskMerchantsProps) {
  const allMonths = Array.from(new Set(records.map(r => r.month))).sort();
  const monthIndex = currentMonth ? allMonths.indexOf(currentMonth) : allMonths.length - 1;

  if (monthIndex < 1) return null; // Need at least 2 months

  const month = allMonths[monthIndex];
  const prevMonth = allMonths[monthIndex - 1];
  const twoMonthsAgo = monthIndex >= 2 ? allMonths[monthIndex - 2] : null;

  // Get revenue by merchant for current and previous months
  const revenueByMonth = new Map<string, Map<string, { name: string; revenue: number }>>();

  [twoMonthsAgo, prevMonth, month].filter(Boolean).forEach(m => {
    const monthData = new Map<string, { name: string; revenue: number }>();
    records.filter(r => r.month === m).forEach(record => {
      const revenue = getRevenue(record);
      const existing = monthData.get(record.merchantId);
      if (existing) {
        existing.revenue += revenue;
      } else {
        monthData.set(record.merchantId, {
          name: record.merchantName,
          revenue
        });
      }
    });
    revenueByMonth.set(m!, monthData);
  });

  const currentData = revenueByMonth.get(month)!;
  const prevData = revenueByMonth.get(prevMonth)!;
  const twoMonthsAgoData = twoMonthsAgo ? revenueByMonth.get(twoMonthsAgo) : null;

  // Identify at-risk merchants
  // CRITICAL: Union current and previous month merchants to catch 100% churn (merchants who disappeared)
  const allMerchantIds = new Set([
    ...Array.from(currentData.keys()),
    ...Array.from(prevData.keys())
  ]);

  const atRisk: AtRiskMerchant[] = [];

  allMerchantIds.forEach(merchantId => {
    const prevRevenue = prevData.get(merchantId)?.revenue;
    if (!prevRevenue || prevRevenue === 0) return; // Skip if no baseline

    const currentRevenue = currentData.get(merchantId)?.revenue || 0; // Treat missing as $0 (churn)
    const merchantName = currentData.get(merchantId)?.name || prevData.get(merchantId)?.name || 'Unknown';

    const declinePercent = ((currentRevenue - prevRevenue) / prevRevenue) * 100;

    // Only flag if declining
    if (declinePercent >= -5) return;

    // Check for consecutive declines
    let consecutiveDeclines = 1;
    if (twoMonthsAgoData) {
      const twoMonthsAgoRevenue = twoMonthsAgoData.get(merchantId)?.revenue;
      if (twoMonthsAgoRevenue && prevRevenue < twoMonthsAgoRevenue) {
        consecutiveDeclines = 2;
      }
    }

    // Determine risk level
    let riskLevel: 'critical' | 'high' | 'medium';
    if (declinePercent <= -50 || (consecutiveDeclines >= 2 && declinePercent <= -20)) {
      riskLevel = 'critical';
    } else if (declinePercent <= -25 || consecutiveDeclines >= 2) {
      riskLevel = 'high';
    } else {
      riskLevel = 'medium';
    }

    atRisk.push({
      merchantId,
      merchantName,
      currentRevenue,
      previousRevenue: prevRevenue,
      declinePercent,
      consecutiveDeclines,
      riskLevel
    });
  });

  // Sort by risk level (critical first) then by decline percent
  const riskPriority = { critical: 0, high: 1, medium: 2 };
  atRisk.sort((a, b) => {
    if (a.riskLevel !== b.riskLevel) {
      return riskPriority[a.riskLevel] - riskPriority[b.riskLevel];
    }
    return a.declinePercent - b.declinePercent;
  });

  const topAtRisk = atRisk.slice(0, limit);

  if (topAtRisk.length === 0) {
    return null;
  }

  const criticalCount = atRisk.filter(m => m.riskLevel === 'critical').length;
  const highCount = atRisk.filter(m => m.riskLevel === 'high').length;

  return (
    <Card className="p-6">
      <div className="flex items-center justify-between mb-4">
        <div>
          <div className="flex items-center gap-2">
            <AlertTriangle className="w-5 h-5 text-red-500" />
            <h3 className="text-lg font-semibold">At-Risk Merchants</h3>
            <Tooltip>
              <TooltipTrigger asChild>
                <button
                  type="button"
                  className="inline-flex"
                  data-testid="help-at-risk-merchants"
                  aria-label="Help: At-Risk Merchants"
                >
                  <HelpCircle className="w-4 h-4 text-muted-foreground/50 hover:text-muted-foreground cursor-help" />
                </button>
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p>Merchants with declining revenue month-over-month. CRITICAL: {'>'}50% drop or 2+ consecutive months with {'>'}20% drop. HIGH: {'>'}25% drop or 2+ consecutive declines. MEDIUM: {'>'}5% drop. Includes merchants who churned completely (100% revenue loss).</p>
              </TooltipContent>
            </Tooltip>
          </div>
          <p className="text-sm text-muted-foreground mt-1">
            {atRisk.length} merchant{atRisk.length !== 1 ? 's' : ''} with declining revenue
          </p>
        </div>
        <div className="flex gap-2">
          {criticalCount > 0 && (
            <Badge variant="destructive" data-testid="badge-critical-count">
              {criticalCount} Critical
            </Badge>
          )}
          {highCount > 0 && (
            <Badge variant="secondary" className="bg-orange-100 dark:bg-orange-950 text-orange-700 dark:text-orange-300" data-testid="badge-high-count">
              {highCount} High
            </Badge>
          )}
        </div>
      </div>

      {criticalCount > 0 && (
        <Alert variant="destructive" className="mb-4">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            {criticalCount} merchant{criticalCount !== 1 ? 's' : ''} require{criticalCount === 1 ? 's' : ''} immediate attention due to severe revenue decline
          </AlertDescription>
        </Alert>
      )}

      <div className="space-y-3">
        {topAtRisk.map((merchant) => (
          <div
            key={merchant.merchantId}
            className={`flex items-start justify-between p-4 rounded-lg border ${
              merchant.riskLevel === 'critical' 
                ? 'bg-red-50 dark:bg-red-950/30 border-red-200 dark:border-red-900'
                : merchant.riskLevel === 'high'
                ? 'bg-orange-50 dark:bg-orange-950/30 border-orange-200 dark:border-orange-900'
                : 'bg-yellow-50 dark:bg-yellow-950/30 border-yellow-200 dark:border-yellow-900'
            }`}
            data-testid={`at-risk-${merchant.merchantId}`}
          >
            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-1">
                <p className="font-medium truncate">{merchant.merchantName}</p>
                <Badge 
                  variant={merchant.riskLevel === 'critical' ? 'destructive' : 'secondary'}
                  className={
                    merchant.riskLevel === 'high'
                      ? 'bg-orange-600 hover:bg-orange-700 text-white'
                      : merchant.riskLevel === 'medium'
                      ? 'bg-yellow-600 hover:bg-yellow-700 text-white'
                      : ''
                  }
                >
                  {merchant.riskLevel.toUpperCase()}
                </Badge>
                {merchant.consecutiveDeclines >= 2 && (
                  <Badge variant="outline" className="text-xs">
                    {merchant.consecutiveDeclines} months down
                  </Badge>
                )}
              </div>
              <p className="text-sm text-muted-foreground">
                {formatCurrency(merchant.previousRevenue)}  {formatCurrency(merchant.currentRevenue)}
              </p>
            </div>
            <div className="flex items-center gap-2 ml-3">
              <div className="text-right">
                <div className="flex items-center gap-1 text-red-600 dark:text-red-400 font-semibold">
                  <TrendingDown className="w-4 h-4" />
                  {formatPercent(Math.abs(merchant.declinePercent), 0)}
                </div>
                <div className="text-xs text-muted-foreground">
                  {formatCurrency(Math.abs(merchant.currentRevenue - merchant.previousRevenue))} loss
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {atRisk.length > limit && (
        <p className="text-sm text-muted-foreground mt-4 text-center">
          + {atRisk.length - limit} more at-risk merchant{atRisk.length - limit !== 1 ? 's' : ''}
        </p>
      )}
    </Card>
  );
}

client/src/components/branch-performance.content:
import { MerchantRecord } from '@shared/schema';
import { Card } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { formatCurrency, formatPercent, getRevenue } from '@/lib/analytics';
import { TrendingUp, TrendingDown, Medal, HelpCircle } from 'lucide-react';

interface BranchMetrics {
  branchId: string;
  totalRevenue: number;
  accountCount: number;
  revenuePerAccount: number;
  retentionRate: number;
  rank: number;
}

interface BranchPerformanceTableProps {
  records: MerchantRecord[];
  currentMonth?: string | null;
}

export function BranchPerformanceTable({ records, currentMonth }: BranchPerformanceTableProps) {
  // Get records for current and previous month
  const allMonths = Array.from(new Set(records.map(r => r.month))).sort();
  const monthIndex = currentMonth ? allMonths.indexOf(currentMonth) : allMonths.length - 1;
  const month = allMonths[monthIndex];
  const prevMonth = monthIndex > 0 ? allMonths[monthIndex - 1] : null;

  if (!month) return null;

  // Group records by branch for current month
  const branchData = new Map<string, { revenue: number; merchants: Set<string> }>();
  const prevBranchData = prevMonth ? new Map<string, Set<string>>() : null;

  records.filter(r => r.month === month && r.branchId).forEach(record => {
    const branch = record.branchId!;
    if (!branchData.has(branch)) {
      branchData.set(branch, { revenue: 0, merchants: new Set() });
    }
    const data = branchData.get(branch)!;
    data.revenue += getRevenue(record);
    data.merchants.add(record.merchantId);
  });

  // Get previous month merchant IDs by branch for retention calculation
  if (prevMonth && prevBranchData) {
    records.filter(r => r.month === prevMonth && r.branchId).forEach(record => {
      const branch = record.branchId!;
      if (!prevBranchData.has(branch)) {
        prevBranchData.set(branch, new Set());
      }
      prevBranchData.get(branch)!.add(record.merchantId);
    });
  }

  // Calculate metrics and retention for each branch
  const branchMetrics: BranchMetrics[] = Array.from(branchData.entries()).map(([branchId, data]) => {
    const accountCount = data.merchants.size;
    const revenuePerAccount = accountCount > 0 ? data.revenue / accountCount : 0;

    // Calculate retention rate
    let retentionRate = 100;
    if (prevBranchData && prevBranchData.has(branchId)) {
      const prevMerchants = prevBranchData.get(branchId)!;
      const retained = Array.from(data.merchants).filter(m => prevMerchants.has(m)).length;
      retentionRate = prevMerchants.size > 0 ? (retained / prevMerchants.size) * 100 : 100;
    }

    return {
      branchId,
      totalRevenue: data.revenue,
      accountCount,
      revenuePerAccount,
      retentionRate,
      rank: 0, // Will be calculated after sorting
    };
  });

  // Sort by revenue and assign ranks
  branchMetrics.sort((a, b) => b.totalRevenue - a.totalRevenue);
  branchMetrics.forEach((branch, index) => {
    branch.rank = index + 1;
  });

  if (branchMetrics.length === 0) {
    return null;
  }

  return (
    <Card className="p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <div className="flex items-center gap-2">
            <h3 className="text-lg font-semibold">Branch Performance Leaderboard</h3>
            <Tooltip>
              <TooltipTrigger asChild>
                <button
                  type="button"
                  className="inline-flex"
                  data-testid="help-branch-performance"
                  aria-label="Help: Branch Performance Leaderboard"
                >
                  <HelpCircle className="w-4 h-4 text-muted-foreground/50 hover:text-muted-foreground cursor-help" />
                </button>
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p>Sales branch ranking by total revenue. Shows merchant account counts, average revenue per account, and retention rates. Top 3 branches receive medals. High retention ({'>'}90%) is excellent; medium (75-90%) needs attention; low ({' <'}75%) requires intervention.</p>
              </TooltipContent>
            </Tooltip>
          </div>
          <p className="text-sm text-muted-foreground mt-1">
            Ranked by total revenue  {month}
          </p>
        </div>
      </div>

      <div className="rounded-md border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead className="w-[60px]">Rank</TableHead>
              <TableHead>Branch ID</TableHead>
              <TableHead className="text-right">Revenue</TableHead>
              <TableHead className="text-right">Accounts</TableHead>
              <TableHead className="text-right">Avg/Account</TableHead>
              <TableHead className="text-right">Retention</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {branchMetrics.map((branch) => (
              <TableRow key={branch.branchId} data-testid={`row-branch-${branch.branchId}`}>
                <TableCell>
                  <div className="flex items-center gap-2">
                    {branch.rank <= 3 && (
                      <Medal className={`w-4 h-4 ${
                        branch.rank === 1 ? 'text-yellow-500' :
                        branch.rank === 2 ? 'text-gray-400' :
                        'text-orange-600'
                      }`} />
                    )}
                    <span className="font-medium">#{branch.rank}</span>
                  </div>
                </TableCell>
                <TableCell>
                  <span className="font-medium">Branch {branch.branchId}</span>
                </TableCell>
                <TableCell className="text-right font-mono">
                  {formatCurrency(branch.totalRevenue)}
                </TableCell>
                <TableCell className="text-right">{branch.accountCount}</TableCell>
                <TableCell className="text-right font-mono">
                  {formatCurrency(branch.revenuePerAccount)}
                </TableCell>
                <TableCell className="text-right">
                  <Badge 
                    variant={branch.retentionRate >= 90 ? 'default' : branch.retentionRate >= 75 ? 'secondary' : 'destructive'}
                    className="font-mono"
                  >
                    {formatPercent(branch.retentionRate, 0)}
                  </Badge>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </Card>
  );
}

client/src/components/revenue-forecast.t.content:
import { MonthlyMetrics } from '@shared/schema';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { formatCurrency } from '@/lib/analytics';
import { TrendingUp, Calendar, BarChart3, HelpCircle } from 'lucide-react';
import { format, parse, addMonths } from 'date-fns';

interface ForecastMonth {
  month: string;
  forecastRevenue: number;
  confidence: 'high' | 'medium' | 'low';
}

interface RevenueForecastProps {
  metrics: MonthlyMetrics[];
}

export function RevenueForecast({ metrics }: RevenueForecastProps) {
  // Filter to only valid yyyy-MM formatted months
  const validMetrics = metrics.filter(m => {
    // Check if month matches yyyy-MM format (e.g., "2024-01")
    return /^\d{4}-\d{2}$/.test(m.month);
  });

  if (validMetrics.length < 3) {
    return null; // Need at least 3 months for forecasting
  }

  // Get last 6 months for trend analysis (or all if less than 6)
  const recentMetrics = validMetrics.slice(-6);

  // Calculate simple linear regression for trend
  const xValues = recentMetrics.map((_, i) => i);
  const yValues = recentMetrics.map(m => m.totalRevenue);

  const n = xValues.length;
  const sumX = xValues.reduce((a, b) => a + b, 0);
  const sumY = yValues.reduce((a, b) => a + b, 0);
  const sumXY = xValues.reduce((sum, x, i) => sum + x * yValues[i], 0);
  const sumX2 = xValues.reduce((sum, x) => sum + x * x, 0);

  // Linear regression: y = mx + b
  const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
  const intercept = (sumY - slope * sumX) / n;

  // Calculate R for confidence
  const yMean = sumY / n;
  const ssTotal = yValues.reduce((sum, y) => sum + Math.pow(y - yMean, 2), 0);
  const ssFit = yValues.map((y, i) => slope * xValues[i] + intercept);
  const ssResidual = yValues.reduce((sum, y, i) => sum + Math.pow(y - ssFit[i], 2), 0);
  // Guard against division by zero when revenue is flat (ssTotal = 0)
  const rSquared = ssTotal === 0 ? 1 : 1 - (ssResidual / ssTotal);

  // Generate 3-month forecast
  const lastMonth = validMetrics[validMetrics.length - 1].month;
  const lastMonthDate = parse(lastMonth, 'yyyy-MM', new Date());

  const forecasts: ForecastMonth[] = [];
  for (let i = 1; i <= 3; i++) {
    const futureMonth = format(addMonths(lastMonthDate, i), 'yyyy-MM');
    const forecastValue = slope * (n + i - 1) + intercept;

    // Determine confidence based on R and how far into future
    let confidence: 'high' | 'medium' | 'low';
    if (rSquared > 0.7 && i === 1) {
      confidence = 'high';
    } else if (rSquared > 0.5 && i <= 2) {
      confidence = 'medium';
    } else {
      confidence = 'low';
    }

    forecasts.push({
      month: futureMonth,
      forecastRevenue: Math.max(0, forecastValue), // Ensure non-negative
      confidence
    });
  }

  // Calculate total forecast and growth rate
  const currentRevenue = recentMetrics[recentMetrics.length - 1].totalRevenue;
  const totalForecast = forecasts.reduce((sum, f) => sum + f.forecastRevenue, 0);
  const avgForecast = totalForecast / 3;
  const growthRate = currentRevenue > 0 ? ((avgForecast - currentRevenue) / currentRevenue) * 100 : 0;

  return (
    <Card className="p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <div className="flex items-center gap-2">
            <BarChart3 className="w-5 h-5 text-primary" />
            <h3 className="text-lg font-semibold">Revenue Forecast</h3>
            <Tooltip>
              <TooltipTrigger asChild>
                <button
                  type="button"
                  className="inline-flex"
                  data-testid="help-revenue-forecast"
                  aria-label="Help: Revenue Forecast"
                >
                  <HelpCircle className="w-4 h-4 text-muted-foreground/50 hover:text-muted-foreground cursor-help" />
                </button>
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p>Statistical 3-month revenue projection using linear regression analysis of recent trends. Confidence levels indicate projection reliability based on historical data correlation (R). Use as strategic planning guidance, not absolute predictions.</p>
              </TooltipContent>
            </Tooltip>
          </div>
          <p className="text-sm text-muted-foreground mt-1">
            3-month projection based on recent trends
          </p>
        </div>
        <Badge variant={growthRate > 0 ? 'default' : 'secondary'} className="text-sm">
          {growthRate > 0 ? '+' : ''}{growthRate.toFixed(1)}% trend
        </Badge>
      </div>

      <div className="grid gap-4 md:grid-cols-3 mb-6">
        {forecasts.map((forecast, index) => (
          <div
            key={forecast.month}
            className="p-4 rounded-lg border bg-card"
            data-testid={`forecast-month-${index + 1}`}
          >
            <div className="flex items-center gap-2 mb-2">
              <Calendar className="w-4 h-4 text-muted-foreground" />
              <p className="text-sm font-medium">
                {format(parse(forecast.month, 'yyyy-MM', new Date()), 'MMM yyyy')}
              </p>
            </div>
            <p className="text-2xl font-bold mb-2" data-testid={`forecast-revenue-${index + 1}`}>
              {formatCurrency(forecast.forecastRevenue)}
            </p>
            <Badge
              variant="outline"
              className={
                forecast.confidence === 'high'
                  ? 'bg-green-50 dark:bg-green-950/30 border-green-200 dark:border-green-900 text-green-700 dark:text-green-300'
                  : forecast.confidence === 'medium'
                  ? 'bg-yellow-50 dark:bg-yellow-950/30 border-yellow-200 dark:border-yellow-900 text-yellow-700 dark:text-yellow-300'
                  : 'bg-gray-50 dark:bg-gray-950/30 border-gray-200 dark:border-gray-900 text-gray-700 dark:text-gray-300'
              }
            >
              {forecast.confidence} confidence
            </Badge>
          </div>
        ))}
      </div>

      <div className="flex items-start gap-3 p-4 rounded-lg bg-muted/50">
        <TrendingUp className="w-5 h-5 text-primary mt-0.5" />
        <div>
          <p className="font-medium">Forecast Methodology</p>
          <p className="text-sm text-muted-foreground mt-1">
            Based on linear trend analysis of the last {recentMetrics.length} months.
            {rSquared >= 0.7 
              ? ' High correlation indicates reliable projection.'
              : rSquared >= 0.5
              ? ' Moderate correlation suggests some variability.'
              : ' Low correlation - forecast should be used cautiously.'}
          </p>
        </div>
      </div>
    </Card>
  );
}

