import { MerchantRecord } from '@shared/schema';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { formatCurrency, formatPercent, getRevenue } from '@/lib/analytics';
import { TrendingUp, TrendingDown, Flame, Snowflake, HelpCircle } from 'lucide-react';

interface TrendingMerchant {
  merchantId: string;
  merchantName: string;
  currentRevenue: number;
  previousRevenue: number;
  changePercent: number;
  changeAmount: number;
}

interface TrendingMerchantsProps {
  records: MerchantRecord[];
  currentMonth?: string | null;
  limit?: number;
}

export function TrendingMerchants({ records, currentMonth, limit = 5 }: TrendingMerchantsProps) {
  const allMonths = Array.from(new Set(records.map(r => r.month))).sort();
  const monthIndex = currentMonth ? allMonths.indexOf(currentMonth) : allMonths.length - 1;
  const month = allMonths[monthIndex];
  const prevMonth = monthIndex > 0 ? allMonths[monthIndex - 1] : null;

  if (!month || !prevMonth) return null;

  // Get revenue by merchant for both months
  const currentMonthData = new Map<string, { name: string; revenue: number }>();
  const prevMonthData = new Map<string, number>();

  records.filter(r => r.month === month).forEach(record => {
    const revenue = getRevenue(record);
    const existing = currentMonthData.get(record.merchantId);
    if (existing) {
      existing.revenue += revenue;
    } else {
      currentMonthData.set(record.merchantId, {
        name: record.merchantName,
        revenue
      });
    }
  });

  records.filter(r => r.month === prevMonth).forEach(record => {
    const revenue = getRevenue(record);
    const existing = prevMonthData.get(record.merchantId) || 0;
    prevMonthData.set(record.merchantId, existing + revenue);
  });

  // Calculate changes for merchants that exist in both months
  const trending: TrendingMerchant[] = [];
  currentMonthData.forEach((data, merchantId) => {
    const previousRevenue = prevMonthData.get(merchantId);
    if (previousRevenue !== undefined && previousRevenue > 0) {
      const changeAmount = data.revenue - previousRevenue;
      const changePercent = (changeAmount / previousRevenue) * 100;

      trending.push({
        merchantId,
        merchantName: data.name,
        currentRevenue: data.revenue,
        previousRevenue,
        changePercent,
        changeAmount
      });
    }
  });

  // Sort by absolute change percent and take top gainers/losers
  const topGainers = trending
    .filter(m => m.changePercent > 0)
    .sort((a, b) => b.changePercent - a.changePercent)
    .slice(0, limit);

  const topLosers = trending
    .filter(m => m.changePercent < 0)
    .sort((a, b) => a.changePercent - b.changePercent)
    .slice(0, limit);

  if (topGainers.length === 0 && topLosers.length === 0) {
    return null;
  }

  return (
    <div className="grid gap-6 lg:grid-cols-2">
      {topGainers.length > 0 && (
        <Card className="p-6">
          <div className="flex items-center gap-2 mb-4">
            <Flame className="w-5 h-5 text-orange-500" />
            <h3 className="text-lg font-semibold">Top Gainers</h3>
            <Tooltip>
              <TooltipTrigger asChild>
                <button
                  type="button"
                  className="inline-flex"
                  data-testid="help-top-gainers"
                  aria-label="Help: Top Gainers"
                >
                  <HelpCircle className="w-4 h-4 text-muted-foreground/50 hover:text-muted-foreground cursor-help" />
                </button>
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p>Merchants with the highest revenue growth this month compared to last month. Identify successful accounts for case studies and best practices to replicate across your merchant base.</p>
              </TooltipContent>
            </Tooltip>
          </div>
          <div className="space-y-3">
            {topGainers.map((merchant) => (
              <div
                key={merchant.merchantId}
                className="flex items-start justify-between p-3 rounded-lg bg-green-50 dark:bg-green-950/30 border border-green-200 dark:border-green-900"
                data-testid={`gainer-${merchant.merchantId}`}
              >
                <div className="flex-1 min-w-0">
                  <p className="font-medium truncate">{merchant.merchantName}</p>
                  <p className="text-sm text-muted-foreground mt-1">
                    {formatCurrency(merchant.previousRevenue)}  {formatCurrency(merchant.currentRevenue)}
                  </p>
                </div>
                <div className="flex items-center gap-2 ml-3">
                  <Badge variant="default" className="bg-green-600 hover:bg-green-700">
                    <TrendingUp className="w-3 h-3 mr-1" />
                    +{formatPercent(merchant.changePercent, 0)}
                  </Badge>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {topLosers.length > 0 && (
        <Card className="p-6">
          <div className="flex items-center gap-2 mb-4">
            <Snowflake className="w-5 h-5 text-blue-500" />
            <h3 className="text-lg font-semibold">Top Decliners</h3>
            <Tooltip>
              <TooltipTrigger asChild>
                <button
                  type="button"
                  className="inline-flex"
                  data-testid="help-top-decliners"
                  aria-label="Help: Top Decliners"
                >
                  <HelpCircle className="w-4 h-4 text-muted-foreground/50 hover:text-muted-foreground cursor-help" />
                </button>
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p>Merchants with the largest revenue decline this month. These accounts may need intervention but haven't reached at-risk thresholds yet. Proactive outreach can prevent further deterioration.</p>
              </TooltipContent>
            </Tooltip>
          </div>
          <div className="space-y-3">
            {topLosers.map((merchant) => (
              <div
                key={merchant.merchantId}
                className="flex items-start justify-between p-3 rounded-lg bg-red-50 dark:bg-red-950/30 border border-red-200 dark:border-red-900"
                data-testid={`decliner-${merchant.merchantId}`}
              >
                <div className="flex-1 min-w-0">
                  <p className="font-medium truncate">{merchant.merchantName}</p>
                  <p className="text-sm text-muted-foreground mt-1">
                    {formatCurrency(merchant.previousRevenue)}  {formatCurrency(merchant.currentRevenue)}
                  </p>
                </div>
                <div className="flex items-center gap-2 ml-3">
                  <Badge variant="destructive">
                    <TrendingDown className="w-3 h-3 mr-1" />
                    {formatPercent(merchant.changePercent, 0)}
                  </Badge>
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}
    </div>
  );
}

client/src/components/upload-tracking.ts.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CheckCircle2, XCircle, AlertCircle, Minus } from "lucide-react";
import { MerchantRecord, UploadedFile, Processor } from "@shared/schema";
import { formatMonthLabel } from "@/lib/analytics";

interface UploadTrackingProps {
  records: MerchantRecord[];
  uploadedFiles: UploadedFile[];
}

// Processor collection start dates (YYYY-MM format)
const PROCESSOR_START_DATES: Record<Processor, string> = {
  'Clearent': '2024-01',
  'ML': '2024-01',
  'Shift4': '2024-01',
  'TSYS': '2024-01',
  'Micamp': '2024-03',          // Started March 2024
  'PayBright': '2025-06',       // Started June 2025
  'TRX': '2024-05',             // Started May 2024
  'Payment Advisors': '2025-01', // Started January 2025
  'All': '2024-01',
};

// Helper to check if a month is before processor's start date
function isBeforeStartDate(processor: string, month: string): boolean {
  const startDate = PROCESSOR_START_DATES[processor as Processor];
  if (!startDate) return false;
  return month < startDate;
}

export function UploadTracking({ records, uploadedFiles }: UploadTrackingProps) {
  // Get all unique months from both records and uploaded files
  const monthsFromRecords = Array.from(new Set(records.map(r => r.month)));
  const monthsFromFiles = Array.from(new Set(uploadedFiles.map(f => f.month)));
  const allMonths = Array.from(new Set([...monthsFromRecords, ...monthsFromFiles])).sort();
  const recentMonths = allMonths.slice(-12); // Last 12 months

  // Derive processors from actual data (records + uploaded files)
  const processorsFromRecords = Array.from(new Set(records.map(r => r.processor as Processor)));
  const processorsFromFiles = Array.from(new Set(uploadedFiles.map(f => f.processor as Processor)));
  const PROCESSORS = Array.from(new Set([...processorsFromRecords, ...processorsFromFiles])).sort();

  // Check if a processor has data for a given month
  const hasData = (processor: Processor, month: string): 'uploaded' | 'missing' | 'partial' | 'before-start' => {
    // Check if month is before processor's start date
    if (isBeforeStartDate(processor, month)) {
      return 'before-start';
    }

    // Get records for this processor-month combination
    const monthRecords = records.filter(r => r.processor === processor && r.month === month);

    if (monthRecords.length === 0) {
      return 'missing';
    }

    // Check if records have valid sales data
    const hasSalesData = monthRecords.some(r => {
      const sales = r.salesAmount ?? 0;
      return sales > 0;
    });

    // If we have records with sales data, it's uploaded
    if (hasSalesData) return 'uploaded';

    // If we have records but no sales data (data quality issue), it's partial
    return 'partial';
  };

  // Calculate statistics based on actual data in recent months
  let expectedCount = 0;
  let uploadedCount = 0;
  let partialCount = 0;
  let missingCount = 0;

  recentMonths.forEach(month => {
    PROCESSORS.forEach(processor => {
      const status = hasData(processor, month);
      if (status !== 'before-start') {
        expectedCount++;
        if (status === 'uploaded') uploadedCount++;
        else if (status === 'partial') partialCount++;
        else if (status === 'missing') missingCount++;
      }
    });
  });

  const stats = {
    totalUploads: uploadedCount,
    expectedUploads: expectedCount,
    partialUploads: partialCount,
    missingUploads: missingCount,
    completionRate: expectedCount > 0 
      ? Math.round((uploadedCount / expectedCount) * 100) 
      : 0,
  };

  return (
    <div className="space-y-6">
      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Complete Data</CardTitle>
            <CheckCircle2 className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-total-uploads">{stats.totalUploads}</div>
            <p className="text-xs text-muted-foreground mt-1">
              Valid sales data
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Data Quality Issues</CardTitle>
            <AlertCircle className="h-4 w-4 text-yellow-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-partial-uploads">{stats.partialUploads}</div>
            <p className="text-xs text-muted-foreground mt-1">
              Records with $0 sales
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Missing Data</CardTitle>
            <XCircle className="h-4 w-4 text-red-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-missing-uploads">
              {stats.missingUploads}
            </div>
            <p className="text-xs text-muted-foreground mt-1">
              No data uploaded
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Completion Rate</CardTitle>
            <CheckCircle2 className="h-4 w-4 text-primary" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-completion-rate">{stats.completionRate}%</div>
            <p className="text-xs text-muted-foreground mt-1">
              Last 12 months
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Upload Tracking Grid */}
      <Card>
        <CardHeader>
          <CardTitle>Monthly Upload Status</CardTitle>
          <CardDescription>
            Track which processors have submitted revenue data each month
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full border-collapse">
              <thead>
                <tr className="border-b">
                  <th className="text-left p-3 font-medium text-sm sticky left-0 bg-background">
                    Processor
                  </th>
                  {recentMonths.map((month) => (
                    <th key={month} className="text-center p-3 font-medium text-sm min-w-[100px]">
                      {formatMonthLabel(month)}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {PROCESSORS.map((processor) => {
                  const processorUploads = recentMonths.filter(m => hasData(processor, m) === 'uploaded').length;
                  const uploadRate = recentMonths.length > 0 
                    ? Math.round((processorUploads / recentMonths.length) * 100) 
                    : 0;

                  return (
                    <tr key={processor} className="border-b hover-elevate" data-testid={`row-processor-${processor.toLowerCase()}`}>
                      <td className="p-3 font-medium sticky left-0 bg-background">
                        <div className="flex items-center justify-between gap-2">
                          <span>{processor}</span>
                          <Badge variant="outline" className="text-xs">
                            {uploadRate}%
                          </Badge>
                        </div>
                      </td>
                      {recentMonths.map((month) => {
                        const status = hasData(processor, month);
                        return (
                          <td key={month} className="text-center p-3">
                            {status === 'uploaded' ? (
                              <CheckCircle2 
                                className="h-5 w-5 text-green-600 mx-auto" 
                                data-testid={`status-${processor.toLowerCase()}-${month}-uploaded`}
                              />
                            ) : status === 'missing' ? (
                              <XCircle 
                                className="h-5 w-5 text-red-600 mx-auto" 
                                data-testid={`status-${processor.toLowerCase()}-${month}-missing`}
                              />
                            ) : status === 'before-start' ? (
                              <Minus 
                                className="h-5 w-5 text-muted-foreground mx-auto" 
                                data-testid={`status-${processor.toLowerCase()}-${month}-before-start`}
                              />
                            ) : (
                              <AlertCircle 
                                className="h-5 w-5 text-yellow-600 mx-auto" 
                                data-testid={`status-${processor.toLowerCase()}-${month}-partial`}
                              />
                            )}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </tbody>
