            </div>
            <div className={`text-2xl font-bold ${isPositive(metrics.netAccountGrowth) ? 'text-green-600' : 'text-red-600'}`} data-testid="value-net-growth">
              {metrics.netAccountGrowth > 0 ? '+' : ''}{metrics.netAccountGrowth.toLocaleString()}
            </div>
          </div>
        </div>
      </div>

      {/* Attrition Analysis */}
      <div className="mb-10">
