import { useState } from 'react';
import { useLocation } from 'wouter';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Shield, Users, User } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

export default function RoleSelector() {
  const [, setLocation] = useLocation();
  const [isSelecting, setIsSelecting] = useState(false);
  const { toast } = useToast();

  const roles = [
    {
      id: 'admin',
      title: 'Administrator',
      description: 'Full access to all data including revenue metrics',
      icon: Shield,
      color: 'from-blue-500 to-blue-600',
    },
    {
      id: 'partner',
      title: 'Partner',
      description: 'Access to merchant data without revenue details',
      icon: Users,
      color: 'from-green-500 to-green-600',
    },
    {
      id: 'agent',
      title: 'Agent',
      description: 'View-only access to merchant information',
      icon: User,
      color: 'from-purple-500 to-purple-600',
    },
  ];

  const selectRole = async (roleId: string) => {
    setIsSelecting(true);
    try {
      await apiRequest('/api/auth/select-role', {
        method: 'POST',
        body: JSON.stringify({ role: roleId }),
        headers: {
          'Content-Type': 'application/json',
        },
      });

      toast({
        title: 'Role selected',
        description: `You are now viewing the dashboard as ${roleId}`,
      });

      setLocation('/');
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to select role',
        variant: 'destructive',
      });
    } finally {
      setIsSelecting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-background to-muted flex items-center justify-center p-4">
      <div className="w-full max-w-5xl">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-[#1A3A52] to-[#7FA848] bg-clip-text text-transparent">
            TRACER C2 Analytics
          </h1>
          <p className="text-muted-foreground text-lg">
            Select your role to view the dashboard
          </p>
        </div>

        <div className="grid md:grid-cols-3 gap-6">
          {roles.map((role) => (
            <Card 
              key={role.id}
              className="group hover-elevate active-elevate-2 cursor-pointer transition-all"
              onClick={() => selectRole(role.id)}
              data-testid={`role-${role.id}`}
            >
              <div className="p-8 text-center">
                <div className={`w-20 h-20 mx-auto mb-6 rounded-full bg-gradient-to-br ${role.color} flex items-center justify-center shadow-lg`}>
                  <role.icon className="w-10 h-10 text-white" />
                </div>

                <h3 className="text-xl font-semibold mb-3">
                  {role.title}
                </h3>

                <p className="text-sm text-muted-foreground mb-6">
                  {role.description}
                </p>

                <Button
                  className="w-full"
                  variant="outline"
                  disabled={isSelecting}
                  data-testid={`button-select-${role.id}`}
                >
                  {isSelecting ? 'Selecting...' : 'Select Role'}
                </Button>
              </div>
            </Card>
          ))}
        </div>

        <div className="mt-12 text-center text-sm text-muted-foreground">
          <p>This is a public demo dashboard. Select any role to explore the features.</p>
        </div>
      </div>
    </div>
  );
}

